mapper.update:
UPDATE offline_store_tb SET store_name = #{storeName},
store_number = #{storeNumber}, store_address = #{storeAddress}, store_address_detail= #{storeAddressDetail}
WHERE id = #{id};

mapper.select:
SELECT totalCount, totalPage, currentPage, CASE WHEN currentPage = 1 THEN 1 ELSE 0 END AS isFirst, CASE WHEN currentPage = totalPage THEN 1 ELSE 0 END AS isLast FROM( SELECT count(*) totalCount, ceil(count(*)/10) totalPage,#{page} currentPage, 0 isFirst, 0 isLast FROM offline_store_tb
WHERE store_address like CONCAT('%',#{keyword},'%')
WHERE store_name like CONCAT('%',#{keyword},'%')
) ost;
