mapper.select:
select *
from order_tb o
where o.id = #{id};

SELECT totalCount, totalPage, currentPage, CASE WHEN currentPage = 1 THEN 1 ELSE 0 END AS isFirst, CASE WHEN currentPage = totalPage THEN 1 ELSE 0 END AS isLast FROM( SELECT count(*) totalCount, ceil(count(*)/10) totalPage,#{page} currentPage, 0 isFirst, 0 isLast FROM order_tb o
WHERE o.id = #{keyword}
LEFT JOIN person_tb p ON p.user_id = o.user_id WHERE p.name like CONCAT('%',#{keyword},'%')
) order_total;
