mapper.select:
select pt.id productId, pt.second_category_id secondCategoryId, pt.product_name productName, pt.price, pt.created_at createdAt, pit.image, sct.second_category_name secondCategoryName, fct.id firstCategoryId, fct.first_category_name firstCategoryName from product_tb pt join product_image_tb pit on pt.id = pit.product_id JOIN second_category_tb sct ON pt.second_category_id = sct.id JOIN first_category_tb fct ON sct.first_category_id = fct.id
pit.is_thumbnail = 1
and first_category_id = #{firstCategoryId}
and second_category_id = #{secondCategoryId}
and pt.product_name like concat('%', #{searchData}, '%')
order by(pt.price) DESC,
order by(pt.price) ASC,
order by(pt.created_at) DESC,
(pit.id) ASC;

SELECT totalCount, totalPage, currentPage, CASE WHEN currentPage = 1 THEN 1 ELSE 0 END AS isFirst, CASE WHEN currentPage = totalPage THEN 1 ELSE 0 END AS isLast FROM( SELECT count(*) totalCount, ceil(count(*)/10) totalPage,#{page} currentPage, 0 isFirst, 0 isLast FROM product_tb
WHERE id = #{keyword}
WHERE product_name like CONCAT('%',#{keyword},'%')
) ost;

SELECT * FROM product_tb

			WHERE id = #{keyword}


			WHERE product_name like CONCAT('%',#{keyword},'%')

ORDER BY id DESC
LIMIT #{startNum}, 10;
